var DKIMKey = require( '..' )
var assert = require( 'assert' )

suite( 'DKIM Key', function() {
  
  suite( 'parse()', function() {
    
    var txtRecord = 'k=rsa; '
    
    test( 'DNS TXT Record', function() {
      var key = DKIMKey.parse( txtRecord )
      assert.ok( key )
      assert.ok(Buffer.isBuffer(key.key))
    })
    
  })
  
})
